function strip(html){
    var doc = new DOMParser().parseFromString(html, 'text/html');
    return doc.body.textContent || "";
 }
export default async function changeLanguage(theRef){
    let lang = prompt('language');
    let res = await (await fetch('https://'.concat(lang.concat('.wikipedia.org')))).text();
    theRef.current.innerHTML = res;

    }
