import { useRef, useEffect } from 'react';
export default function useEventListener(eventName, handler,onName,offName, element = window){
    // Create a ref that stores handler
    const savedHandler = useRef();
    
    // Update ref.current value if handler changes.
    // This allows our effect below to always get latest handler ...
    // ... without us needing to pass it in effect deps array ...
    // ... and potentially cause effect to re-run every render.
    useEffect(() => {
      savedHandler.current = handler;
    }, [handler]);
  
    useEffect(
      () => {
        // Make sure element supports addEventListener
        // On 
        const isSupported = element && element[onName];
        if (!isSupported) return;
        
        // Create event listener that calls handler function stored in ref
        const eventListener = (...es) => savedHandler.current(...es);
        
        // Add event listener
        element[onName](eventName, eventListener);
        
        // Remove event listener on cleanup
        return () => {
          element[offName](eventName, eventListener);
        };
      },
      [eventName, element] // Re-run if eventName or element changes
    );
  };
