import {get} from 'https'
export default (req,res) => {
    const {
        query: { lid },
      } = req;
      const url = 'https://' + lid + '.wikipedia.org';
      get(url,r => get(r.headers.location,r => r.on('end',res.end.bind(res)).pipe(res,{end: false})));

}
